import { useState } from 'react';
import { Button, Modal } from 'react-bootstrap';

export const ResumeButton = ({ className }) => {
    const [show, setShow] = useState(false);

    const handleShow = () => setShow(true);
    const handleClose = () => setShow(false);

    return (
        <>
            <button className={className} onClick={handleShow}>
                View Resume
            </button>

            <Modal size="lg" show={show} onHide={handleClose} centered>
                <Modal.Header closeButton>
                    <Modal.Title>Resume Preview</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <object
                        data="path_to_your_resume.pdf" 
                        type="application/pdf" 
                        width="100%"
                        height="600px"
                    >
                        <p>It appears you don't have a PDF plugin for this browser. You can <a href="path_to_your_resume.pdf">click here to download the PDF file.</a></p>
                    </object>
                </Modal.Body>
            </Modal>
        </>
    );
};
