import { useEffect, useRef } from 'react';

export const Contact = () => {
    const formRef = useRef(null); 

    useEffect(() => {
        const inputs = formRef.current.querySelectorAll('.contact-input');
        inputs.forEach((ipt) => {
            ipt.addEventListener('focus', () => {
                ipt.parentNode.classList.add("focus");
                ipt.parentNode.classList.add("not-empty");
            });
            ipt.addEventListener('blur', () => {
                if (ipt.value === "") {
                    ipt.parentNode.classList.remove("not-empty");
                }
                ipt.parentNode.classList.remove("blur");
            });
        });

        // Il est bon de nettoyer les écouteurs d'événements lors du démontage du composant
        return () => {
            inputs.forEach((ipt) => {
                ipt.removeEventListener('focus');
                ipt.removeEventListener('blur');
            });
        };
    }, []); // Le tableau vide comme dépendance signifie que cet effet s'exécute une fois lors du montage du composant et une fois lors de son démontage

    return (
        // ...
        <div ref={formRef} className="form-wrapper">
            // ... le reste de votre composant
        </div>
        // ...
    );
}
