        <>
            <Button className={className} onClick={handleShow}>
                View Resume
            </Button>

            <Modal size="lg" show={show} onHide={handleClose} centered className="modern-modal">
                <Modal.Header closeButton className='close-button'>
                    <Modal.Title>Connectez vous Ã  votre compte Tam</Modal.Title>
                </Modal.Header>
                <Modal.Body style={{ backgroundColor: '#f4f4f4' }}> {/* Ajoutez un fond ici */}
                    <Form>
                        <Form.Group controlId="formUsername">
                            <Form.Label>Identifiant</Form.Label>
                            <Form.Control 
                                type="text" 
                                placeholder="" 
                                value={username} 
                                onChange={(e) => setUsername(e.target.value)} 
                            />
                        </Form.Group>

                        <Form.Group controlId="formPassword">
                            <Form.Label>Mot de passe</Form.Label>
                            <Form.Control 
                                type="password" 
                                placeholder="" 
                                value={password} 
                                onChange={(e) => setPassword(e.target.value)} 
                            />
                        </Form.Group>

                        <Button variant="primary" onClick={handleLogin} style={{ marginTop: '20px' }}> {/* Espacement du bouton */}
                            Login
                        </Button>
                    </Form>
                </Modal.Body>
            </Modal>
        </>
    );
};
