import React, { useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import headerImg from "../assets/img/pp.jpg";
import Typed from "typed.js";

export const Banner = () => {

    useEffect(() => {
        // Options pour Typed.js
        const options = {
            strings: ["Web Developer", "Game Developer", "Video Editor"],
            typeSpeed: 100,
            backSpeed: 60,
            loop: true
        };

        // Initialisation de Typed.js
        const typed = new Typed(".typing", options);

        // Cleanup à la destruction du composant
        return () => {
            typed.destroy();
        };
    }, []);

    return (
        <section className="hidden">
            <section className="banner" id="home">
                <Container>
                    <Row className="align-items-center">
                        <Col xs={12} md={6} xl={7}>
                            <h2 className="text-1">Hello, I'm</h2> {/* Changement de div en h2 et ajout de className */}
                            <h1 className="text-2">Lence</h1> {/* Changement de div en h1 et ajout de className */}
                            <h2 className="text-3">I'm a<span className="mini_space"></span> <span className="typing"></span></h2> {/* Maintien de div et ajout de className */}
                        </Col>
                        <Col xs={12} md={6} xl={4}>
                            <img src={headerImg} alt="Header Img" className="img-fluid" />
                        </Col>
                    </Row>
                    <div className="top-margin">
                        <h2 className="">About me</h2>
                        <p>My journey in the world of tech began at Epitech, where I spent two foundational years.<br></br> Currently, I'm pursuing my third year at EPSI, delving deeper into the realm of application design as part of their "Concepteur Développeur d’Applications" program.Seeking a one-year apprenticeship.</p>
                    </div>
                </Container>
            </section>
        </section>

    )
}
